const Koa = require("koa")
const Router = require("koa-router")
const bodyParser = require("koa-bodyparser")
const net = require("net")
const debug = require("debug")("eoh:test_util")

const routing = koa => routingFn => {
  const router = new Router()
  routingFn(router)
  koa.use(router.routes())
}

const startServer = async t => {
  const port = await getPort()
  const koa = new Koa()
  koa.use(bodyParser())
  const server = koa.listen(port)
  t.context = {
    koa,
    port,
    server,
  }
}

const stopServer = async t => {
  if (t && t.context && t.context.server) {
    await t.context.server.close()
  }
}

// private

const getPort = async () => {

  return new Promise((resolve, reject) => {

    let port = Math.floor(Math.random() * (60000 - 5000 + 1) + 5000)
    const server = net.createServer()
    server.on("error", error => {
      debug("got error:", error)
      server.close(() => {
        port += 1
        if (error.code !== "EADDRINUSE" || port >= 65535) {
          server.removeAllListeners()
          reject(error)
        }
        setImmediate(() => {
          server.listen(port)
        })
      })
    })
    server.on("listening", () => {
      debug("connected:", port)
      server.removeAllListeners()
      server.close(() => {
        resolve(port)
      })
    })
    debug("try connect:", port)
    server.listen(port)
  })
}

module.exports = {
  startServer,
  stopServer,
  routing
}

const debug = require("debug")("eoh:oauth1")
const crypto = require("crypto")
const {
  postRequest,
} = require("../utils")

/**
 *
 * @param {string} method
 * @param {string} url
 * @param {object} parameter
 * @param {string} consumer_secret
 * @param {string} token_secret
 */
const createSignature = (method, url, parameter, consumer_secret, token_secret) => {

  const sorted = Object.keys(parameter).map(key => {
    // eslint-disable-next-line
    return { key: encodeURIComponent(key), value: encodeURIComponent(parameter[key]) }
  })
    .sort((a, b) => {
      return a.key.localeCompare(b.key)
    })

  const output = sorted.reduce((prev, current) => {
    if (prev.length !== 0) {
      prev = `${prev}&`
    }
    return `${prev}${current.key}=${current.value}`
  }, "")

  const signatureBaseString = `${method.toUpperCase()}&${encodeURIComponent(url)}&${encodeURIComponent(output)}`

  const signKey = createSignKey(consumer_secret, token_secret)

  const signature = crypto.createHmac("sha1", signKey).update(signatureBaseString).digest("base64")

  return signature
}

const createSignKey = (consumer_secret, token_secret) => {
  const encoded_cs = encodeURIComponent(consumer_secret || "")
  const encoded_ts = encodeURIComponent(token_secret || "")
  return `${encoded_cs}&${encoded_ts}`
}

const createOAuthHeader = request => {

  const INIT = "OAuth "
  const dst = Object.keys(request)
    .sort((a, b) => {
      return a.localeCompare(b)
    })
    .reduce((prev, current) => {
      // eslint-disable-next-line
      if (!request[current]) {
        return prev
      }
      const ekey = encodeURIComponent(current)
      // eslint-disable-next-line
      const evalue = `"${encodeURIComponent(request[current])}"`
      if (prev !== INIT) {
        prev = `${prev}, `
      }
      return `${prev}${ekey}=${evalue}`
    }, INIT)

  return dst
}

/**
 * @param {string} url
 * @param {object} parameter - POST parameter
 * @returns {Promise<string>}
 */
const oauthPOSTRequest = (url, parameter, oauth) => {

  const oauthParameter = Object.assign({}, parameter, {
    oauth_consumer_key: oauth.oauth_consumer_key || "",
    oauth_timestamp: oauth.oauth_timestamp || Math.floor((new Date().getTime() / 1000)),
    oauth_version: oauth.oauth_version || "1.0",
    oauth_nonce: oauth.oauth_nonce || crypto.createHash("sha256").update(`${parameter}`).digest("base64"),
    oauth_signature_method: oauth.oauth_signature_method || "HMAC-SHA1"
  })

  if (oauth.oauth_callback) {
    oauthParameter.oauth_callback = oauth.oauth_callback
  }

  if (oauth.oauth_token) {
    oauthParameter.oauth_token = oauth.oauth_token
  } else if (oauth.oauth_request_token) {
    oauthParameter.oauth_token = oauth.oauth_request_token
  }

  const signature = createSignature("POST", url, oauthParameter, oauth.oauth_consumer_secret, oauth.oauth_token_secret)
  oauthParameter.oauth_signature = signature

  const oauthHeader = createOAuthHeader(oauthParameter)
  debug("OAuth header", oauthHeader)

  return postRequest(url, parameter, {
    "Authorization": oauthHeader,
  })
}

module.exports = {
  oauthPOSTRequest
}

const Url = require("url")
const querystring = require("querystring")
const debug = require("debug")("eoh:oauth1")
const { BrowserWindow } = require("electron").remote || require("electron")

const {
  awaitRedirect,
} = require("../utils")

const {
  oauthPOSTRequest,
} = require("./helper")

class OAuth1Provider {

  /**
   * @param {object} config - OAuth1 config.
   */
  constructor(config) {
    this.config = config
  }

  /**
   * @param {Electron.BrowserWindow} window
   * @returns {Promise<any>}
   */
  perform(window) {

    const config = this.config

    this.finished = false
    return new Promise((resolve, reject) => {

      // Step 1: Obtaining a request token
      return oauthPOSTRequest(config.request_token_url, {}, {
        oauth_callback: config.callback_url,
        oauth_consumer_key: config.oauth_consumer_key,
        oauth_consumer_secret: config.oauth_consumer_secret,
      })
        .then(query => {
          const resp = querystring.parse(query)
          debug("Step 1 response", resp)
          if (!window) {

            // Backward compatibility
            window = new BrowserWindow({
              width: 600,
              height: 800,
              webPreferences: {
                nodeIntegration: false,
                contextIsolation: true,
              },
            })
            // eslint-disable-next-line
            console.warn(`In the future release the "window" parameter will be mandatory when grant_type is ${type}!`)
            // next release: return Promise.reject(new Error(`if grant-type is "${type}", window is required.`))
          }

          window.once("close", () => {
            if (this.finished === false) {
              reject(new Error("window closed"))
            }
          })

          // Step 2: Redirecting the user
          const url = `${config.authenticate_url}?oauth_token=${resp.oauth_token}`
          window.loadURL(url)
          return awaitRedirect(config.callback_url, window.webContents, debug)
        })
        .then(resp => {

          debug("Step 2 response", resp)
          // Step 3: Converting the request token to an access token
          const query = Url.parse(resp, true).query
          if (!query.oauth_token || query.oauth_token === "denied" || !query.oauth_verifier) {
            this.finished = true
            return Promise.reject(new Error("User denied or invalid response"))
          }
          return oauthPOSTRequest(config.access_token_url,
            { oauth_verifier: query.oauth_verifier },
            {
              oauth_consumer_key: config.oauth_consumer_key,
              oauth_consumer_secret: config.oauth_consumer_secret,
              oauth_token: query.oauth_token
            })
        })
        .then(resp => {
          debug("Step 3 response", resp)
          this.finished = true
          resolve(querystring.parse(resp))
        })
        .catch(error => reject(error))

    })
  }
}

module.exports = OAuth1Provider

const querystring = require("querystring")
const Url = require("url")

const debug = require("debug")("eoh:oauth2")

const {
  postRequest,
  awaitRedirect,
  omit,
} = require("../utils")

const valueForType = (type, codeFn, tokenFn, passFn, credFn) => {

  if (!type) {
    throw new Error("type must not be null.")
  }

  if (type.includes("id_token")) {
    // eslint-disable-next-line
    console.warn("Multiple-Valued Response Type Combinations is not supportted. Brobably not working...")
  }

  if (type.includes("code")) {
    return codeFn(type)
  }

  if (type.includes("token")) {
    return tokenFn(type)
  }

  if (type.includes("password")) {
    return passFn(type)
  }

  if (type.includes("client_credentials")) {
    return credFn(type)
  }

  throw new Error(`Unsupported type: ${type}`)
}

const createAuthorizeUrl = (config, optional) => {

  const keys = [
    "client_id",
    "redirect_uri",
    "scope",
    "state",
    "response_type",
  ]
  /* eslint-disable security/detect-object-injection */
  const parameter = Object.assign({}, keys.reduce((prev, key) => {
    if (typeof config[key] === "string") {
      prev[key] = config[key]
    }
    return prev
  }, {}), optional || {})
  /* eslint-enable */

  const url = `${config.authorize_url}?${querystring.stringify(parameter)}`
  return url
}

/**
 *
 * @param {object} config
 * @param {Electron.BrowserWindow} window
 * @param {object} auth  - Custom auth parameter.
 * @param {object} token  - Custom token parameter.
 */
const authorizationCodeFlowTask = (config, window, auth, token) => {

  if (!window) {
    return Promise.reject(new Error("window is required"))
  }

  const authorizeUrl = createAuthorizeUrl(config, auth)
  window.loadURL(authorizeUrl)

  return awaitRedirect(config.redirect_uri, window.webContents)
    .then(url => {
      debug(`redirect url: "${url}"`)

      const query = Url.parse(url, true).query

      if (!query) {
        return Promise.reject(new Error(`invalid response: ${url}`))
      }

      if (query.error) {
        const error = new Error("error response")
        error.query = query
        return Promise.reject(error)
      }

      if (!query.code) {
        return Promise.reject(new Error("missing 'code' response."))
      }

      if (config.state && !query.state) {
        return Promise.reject(new Error("missing 'state' response."))
      }

      const parameter = Object.assign({}, {
        client_id: config.client_id,
        grant_type: "authorization_code",
        code: query.code,
      }, token || {})

      if (config.client_secret) {
        parameter.client_secret = config.client_secret
      }

      if (config.redirect_uri) {
        parameter.redirect_uri = config.redirect_uri
      }

      if (query.state) {
        parameter.state = query.state
      }

      return postRequest(config.access_token_url, parameter)
    })
}

/**
 *
 * @param {object} config
 * @param {Electron.BrowserWindow} window
 * @param {object} auth - Custom token parameter.
 */
const implicitFlowTask = (config, window, auth) => {

  if (!window) {
    return Promise.reject(new Error("window is required"))
  }

  const authorizeUrl = createAuthorizeUrl(config, auth)
  window.loadURL(authorizeUrl)

  return awaitRedirect(config.redirect_uri, window.webContents, debug)
    .then(url => {
      debug(`redirect url: "${url}"`)
      const hash = Url.parse(url, false).hash.replace(/^#/, "")
      return querystring.parse(hash)
    })
}

/**
 *
 * @param {object} config
 * @param {Electron.BrowserWindow} window - This is not used.
 * @param {object} auth - This is not used.
 * @param {object} token - Custom token parameter.
 */
const resourceOwnerPasswordCredentialsFlowTask = (config, window, auth, token) => {
  const parameter = omit(config, "access_token_url")
  return postRequest(config.access_token_url, parameter, token)
}

/**
 *
 * @param {object} config
 * @param {object} auth - Custom auth parameter. This is not used.
 * @param {object} token - Custom token parameter.
 */
const clientCredentialsFlowTask = (config, auth, token) => {
  const parameter = omit(config, "access_token_url")
  return postRequest(config.access_token_url, parameter, token)
}

/**
 * @param {object} config
 */

const validate = config => {
  const type = config.response_type || config.grant_type || "code"
  /* @type {[string]} */
  const keys = valueForType(type,
    () => {
      return [
        "client_id",
        "authorize_url",
        "access_token_url",
        "redirect_uri",
      ]
    },
    () => {
      return [
        "client_id",
        "authorize_url",
        "redirect_uri",
      ]
    },
    () => {
      return [
        "access_token_url",
        "username",
        "password",
      ]
    },
    () => {
      return [
        "access_token_url",
      ]
    })
  const test = k => {
    if (typeof k === "string") {
      k = [k]
    }
    // eslint-disable-next-line
    return k.find(key => typeof config[key] === "string")
  }
  const missing = keys.find(k => !test(k))
  if (missing) {
    return new Error(`${missing} is required for ${type}`)
  }
}

/**
 * @param {string} type
 * @returns {function|undefined}
 */
const mapGrantTypeToTask = type => (
  valueForType(type,
    () => {
      return authorizationCodeFlowTask
    },
    () => {
      return implicitFlowTask
    },
    () => {
      return resourceOwnerPasswordCredentialsFlowTask
    },
    () => {
      return clientCredentialsFlowTask
    })
)

/**
 *
 * @param {string} type
 *
 * @returns {boolean|undefined}
 *
 */
const needWindowForGrantType = type => (
  valueForType(type,
    () => {
      return true
    },
    () => {
      return true
    },
    () => {
      return false
    },
    () => {
      return false
    })
)

module.exports = {
  validate,
  mapGrantTypeToTask,
  needWindowForGrantType,
}

const { BrowserWindow } = require("electron").remote || require("electron")

const {
  validate,
  mapGrantTypeToTask,
  needWindowForGrantType,
} = require("./helper")

class OAuth2Provider {

  /**
   * @param {object} config - OAuth2 config.
   */
  constructor(config) {
    const error = validate(config)
    if (error) {
      throw error
    }
    this.config = config
    this.customAuthorizationRequestParameter = {}
    this.customAccessTokenRequestParameter = {}

    /** @type {boolean} */
    this.finished = false
  }

  /**
   * Sets the custom parameter for Authorization Request.
   *
   * This can be used to include your own parameters in your request
   * that are not included in the OAuth2 specification.
   *
   * @example
   * ```js
   *  const provider = new OAuth2Provider({})
   *    .withCustomAuthorizationRequestParameter({hoge: "huga"})
   *    .withCustomAccessTokenRequestParameter({foo: "bar"})
   * ```
   *
   * @param {object} param
   */
  withCustomAuthorizationRequestParameter(param) {
    this.customAuthorizationRequestParameter = param
    return this
  }

  /**
   * Sets the custom parameter for  Access Token Request.
   *
   * This can be used to include your own parameters in your request
   * that are not included in the OAuth2 specification.

   * @example
   * ```js
   *  const provider = new OAuth2Provider(config)
   *    .withCustomAuthorizationRequestParameter({hoge: "huga"})
   *    .withCustomAccessTokenRequestParameter({foo: "bar"})
   * ```
   *
   * @param {object} param
   */
  withCustomAccessTokenRequestParameter(param) {
    this.customAccessTokenRequestParameter = param
    return this
  }

  /**
   * Initiate OAuth2 flow.
   *
   * if grant_type is "code" or "token", you need to pass "window" argument.
   * In other cases it is not necessary.
   *
   * @example
   * ```js
   *  const window = new BrowserWindow({
   *    width: 600,
   *    height: 800,
   *    webPreferences: {
   *      nodeIntegration: false // We recommend disabling nodeIntegration for security.
   *      contextIsolation: true // We recommend enabling contextIsolation for security.
   *      // see https://github.com/electron/electron/blob/master/docs/tutorial/security.md
   *    },
   *  })
   *  const provider = new OAuth2Provider(config)
   *  provider.perform(window)
   *    .then(console.log)
   *    .catch(console.error)
   * ```
   *
   * @param {Electron.BrowserWindow=} window
   * @returns {Promise<any>}
   */
  perform(window) {

    const config = this.config
    /** @type {string} */
    const type = config.response_type || config.grant_type || "code"
    const task = mapGrantTypeToTask(type)

    if (needWindowForGrantType(type)) {
      if (!window) {
        // Backward compatibility
        window = new BrowserWindow({
          width: 600,
          height: 800,
          webPreferences: {
            nodeIntegration: false,
            contextIsolation: true,
          },
        })
        // eslint-disable-next-line
        console.warn(`In the future release the "window" parameter will be mandatory when grant_type is ${type}!`)
        // next release: return Promise.reject(new Error(`if grant-type is "${type}", window is required.`))
      }
      this.finished = false

      window.once("show", () => {
        window.once("close", () => {
          if (this.finished === false) {
            this.userCancelError = new Error("User cancelled")
          }
        })
      })
    }

    return task(config, window, this.customAuthorizationRequestParameter, this.customAccessTokenRequestParameter)
      .then(resp => {
        this.finished = true

        if (this.userCancelError) {
          return Promise.reject(this.userCancelError)
        }

        if (resp.error) {
          return Promise.reject(resp)
        }

        return Promise.resolve(resp)
      })
  }
}

module.exports = OAuth2Provider

