const {
  convertUsefulObject,
} = require("./request")
const post = require("./request").postRequest

const postRequest = (...args) => post(...args).then(convertUsefulObject)

const {
  awaitRedirect,
} = require("./redirect")

const {
  pick,
  omit,
} = require("./util")

module.exports = {
  postRequest,
  awaitRedirect,
  pick,
  omit,
}

const debug = require("debug")("eoh:helper")

/**
 *
 * @param {string} redirectURL
 * @param {Electron.WebContents} webContents
 */
const awaitRedirect = (redirectURL, webContents) => {

  if (!redirectURL || !webContents) {
    return Promise.reject(new Error("Invalid parameter"))
  }

  /**
   * @param {string} url
   */
  const isRedirectURL = url => {
    return url.startsWith(redirectURL)
  }

  return new Promise(resolve => {

    const testWillNavigate = (event, url) => {
      debug("on will-navigate:", url)
      if (isRedirectURL(url)) {
        off()
        resolve(url)
      }
    }

    const testDidGetRedirectRequest = (event, oldUrl, newUrl) => {
      debug("on did-get-redirect-request:", newUrl)
      if (isRedirectURL(newUrl)) {
        off()
        resolve(newUrl)
      }
    }

    const off = () => {
      webContents.removeListener("will-navigate", testWillNavigate)
      webContents.removeListener("did-get-redirect-request", testDidGetRedirectRequest)
    }

    webContents.on("will-navigate", testWillNavigate)
    webContents.on("did-get-redirect-request", testDidGetRedirectRequest)
  })
}

module.exports = {
  awaitRedirect
}

const querystring = require("querystring")
const net = require("electron").net || require("electron").remote.net

/**
 * @param {string} url
 * @param {object} parameter - post data
 * @param {object} headers
 * @returns {Promise<{statusCode: number, statusMessage: string, headers: object, body: Buffer}>}
 */
const postRequest = (url, parameter, headers) => {

  return new Promise((resolve, reject) => {

    const postData = querystring.stringify(parameter)

    const request = net.request({
      url,
      method: "POST",
      headers: Object.assign({}, {
        "Content-Type": "application/x-www-form-urlencoded",
        "Content-Length": Buffer.byteLength(postData)
      }, headers || {})
    })

    request.on("response", response => {

      const datas = []

      response.on("data", chunk => {
        datas.push(chunk)
      })

      response.on("end", () => {
        const body = Buffer.concat(datas)
        const resp = {
          headers: response.headers,
          statusCode: response.statusCode,
          statusMessage: response.statusMessage,
          body: body,
        }
        resolve(resp)
      })

      response.on("error", error => {
        reject(error)
      })
    })

    request.write(postData, "utf8")
    request.end()
  })
}

/**
 * @param response {object}
 * @param response.headers {object}
 * @param response.statusCode {number}
 * @param response.statusMessage {string}
 *
 * @returns Promise<object>
 */
const convertUsefulObject = response => {

  response.body = response.body.toString("utf8")

  if (response.statusCode >= 400) {

    const error = new Error(`Error response: ${response.statusCode} - ${response.statusMessage}`)
    error.response = response
    return Promise.reject(error)
  }

  if (response.headers["content-type"].join(" ").includes("application/json")) {
    response.body = JSON.parse(response.body)
  }

  return response.body
}

module.exports = {
  convertUsefulObject,
  postRequest,
}

/**
 *
 * @param {object} obj
 * @param {string|[string]} props
 */
const omit = (obj, props) => {

  obj = obj || {}
  props = props || []

  if (typeof props === "string") {
    props = [props]
  }

  return Object.keys(obj).reduce((prev, key) => {
    if (!props.includes(key)) {
      // eslint-disable-next-line
      prev[key] = obj[key]
    }
    return prev
  }, {})
}

/**
 *
 * @param {object} obj
 * @param {string|[string]} props
 */
const pick = (obj, props) => {

  obj = obj || {}
  props = props || []

  if (typeof props === "string") {
    props = [props]
  }

  return Object.keys(obj).reduce((prev, key) => {
    if (props.includes(key)) {
      // eslint-disable-next-line
      prev[key] = obj[key]
    }
    return prev
  }, {})
}

module.exports = {
  omit,
  pick,
}

# Electron OAuth Helper

[![Greenkeeper badge](https://badges.greenkeeper.io/mironal/electron-oauth-helper.svg)](https://greenkeeper.io/)

[![NPM](https://nodei.co/npm/electron-oauth-helper.png)](https://nodei.co/npm/electron-oauth-helper/)

[![Build Status](https://travis-ci.org/mironal/electron-oauth-helper.svg)](https://travis-ci.org/mironal/electron-oauth-helper)

## What's this

Easy to use helper library for OAuth1 and OAuth2.

All grant type supported.

- Authorization Code Grant
- Implicit Grant
- Resource Owner Password Credentials Grant
- Client Credentials Grant

> TODO: Refreshing an Access Token

You can get a token just by calling a method of start OAuth.

This library is lightweight because it depends only on [debug](https://github.com/visionmedia/debug) module.

## Install

`npm install electron-oauth-helper --save`

## Usage

### OAuth1

```js

const { OAuth1Provider } = require("electron-oauth-helper")

const window = new BrowserWindow({
  width: 600,
  height: 800,
  webPreferences: {
    nodeIntegration: false // We recommend disabling nodeIntegration for security.
    contextIsolation: true // We recommend enabling contextIsolation for security.
    // see https://github.com/electron/electron/blob/master/docs/tutorial/security.md
  },
})

const config = { /* oauth config. please see example/main/config.example.js.  */}
const provider = new OAuth1Provider(config)
provider.perform(window)
  .then(resp => {
    console.log(resp)
  })
  .catch(error => console.error(error))
```

### OAuth2

```js

const { OAuth2Provider } = require("electron-oauth-helper")

const window = new BrowserWindow({
  width: 600,
  height: 800,
  webPreferences: {
    nodeIntegration: false // We recommend disabling nodeIntegration for security.
    contextIsolation: true // We recommend enabling contextIsolation for security.
    // see https://github.com/electron/electron/blob/master/docs/tutorial/security.md
  },
})

const config = { /* oauth config. please see example/main/config.example.js.  */}
const provider = new OAuth2Provider(config)
// Your can use custom parameter.
// const provider = new OAuth2Provider(config)
//   .withCustomAuthorizationRequestParameter({})
//   .withCustomAccessTokenRequestParameter({})
provider.perform(window)
  .then(resp => {
    console.log(resp)
  })
